import Vue from "vue";
import Router from "vue-router";
// import Home from "../components/Home.vue";

Vue.use(Router);

export default new Router({
  routes: [
    {
      path: "/",
      redirect: "/login",
      component: () => import("@/components/Login"),
    },
    {
      path: "/login",
      name: "Login",
      component: () => import("@/components/Login"),
    },
    {
      path: "*",
      name: "Notfound",
      component: () => import("@/components/Notfound"),
    },
    // {
    //   path: "/home",
    //   // component: () => import("@/components/Home"), // 懒加载不用在一开始import组件了
    //   component: (resolve) => require(["@/components/Home"], resolve),
    // },
    {
      path: "/home",
      name: "StudentManagement",
      iconClass: "fa fa-users",
      redirect: "/home/student",
      component: () => import("@/components/Home"),
      children: [
        {
          path: "/home/student",
          name: "StudentList",
          iconClass: "fa fa-list",
          component: () => import("@/components/Student/StudentList"),
        },
        {
          path: "/home/info",
          name: "InfoList",
          iconClass: "fa fa-list-alt",
          component: () => import("@/components/Student/InfoList"),
        },
        {
          path: "/home/infomanagement",
          name: "InfoManagement",
          iconClass: "fa fa-list-alt",
          component: () => import("@/components/Student/InfoManagement"),
        },
        {
          path: "/home/worklist",
          name: "WorkList",
          iconClass: "fa fa-list-ul",
          component: () => import("@/components/Student/WorkList"),
        },
        {
          path: "/home/workmanagement",
          name: "WorkManagement",
          iconClass: "fa fa-th-list",
          component: () => import("@/components/Student/WorkManagement"),
        },
      ],
    },
    {
      path: "/home",
      name: "DataAnalysis",
      iconClass: "fa fa-bar-char",
      component: () => import("@/components/Home"),
      children: [
        {
          path: "/home/dataview",
          name: "DataView",
          iconClass: "fa fa-fa-chart",
          component: () => import("@/components/DataAnalysis/DataView"),
        },
        {
          path: "/home/mapview",
          name: "MapView",
          iconClass: "fa fa-line-chart",
          component: () => import("@/components/DataAnalysis/MapView"),
        },
        {
          path: "/home/infomanagement",
          name: "InfoManagement",
          iconClass: "fa fa-line-chart",
          component: () => import("@/components/DataAnalysis/InfoManagement"),
        },
        {
          path: "/home/student",
          name: "WorkList",
          iconClass: "fa fa-line-chart",
          component: () => import("@/components/DataAnalysis/WorkList"),
        },
      ],
    },
  ],
  mode: "history",
});
