//项目中大多数会把大多数接口请求封装成api来调用
import service from "@/service";

// 登录接口封装
export function login(data) {
  return service({
    method: "post",
    url: "/login",
    data: data,
  });
}
// 学生列表查询接口封装
export function students(params) {
  return service({
    method: "get",
    url: "/students",
    params: params,
  });
}

// 学生列表删除接口
export function studentDelete(id) {
  return service({
    method: "delete",
    url: `/students/${id}`,
  });
}

// 学生列表 查询和重置接口
