//项目中大多数会把大多数接口请求封装成api来调用
import service from "@/service";
import qs from "qs";

// 登录接口封装
export function login(data) {
  return service({
    method: "post",
    url: "/login",
    data: data,
  });
}
// 学生列表查询接口封装
export function students(params) {
  return service({
    method: "get",
    url: "/students",
    params: params,
  });
}

// 学生列表删除接口
export function studentDelete(id) {
  return service({
    method: "delete",
    url: `/students/${id}`,
  });
}

// 信息列表 新增接口
export function info(data) {
  data = qs.stringify(data);
  return service({
    method: "post",
    url: "/info",
    data: data,
  });
}

// 信息列表 查询接口
export function getInfo() {
  return service({
    method: "get",
    url: "/info",
    data,
  });
}
