// 封装infoManagement里面类似的操作

// 获取表格数据
export function getData(root, url, params) {
  root.service
    .get(url, { params: params || {} })
    .then((res) => {
      if (res.status === 200) {
        root.tableData = res.data.data;
        root.total = res.data.total;
        root.$message({ message: res.data.message, tyep: "success" });
      }
    })
    .catch((err) => {
      throw err;
    });
}
